/*
	
	JavaScript epoch time converter number method
	http://kylerush.net/blog/javascript-epoch-time-converter-number-method/
	By : Kyle Rush

*/
Number.prototype.timeLeft=function(){var days=Math.floor(this/86400);var hours=Math.floor((this-(days*86400))/3600);var minutes=Math.floor((this-((hours*3600)+(days*86400)))/60);var seconds=this-((days*86400)+(hours*3600)+(minutes*60));var result=new String();if((days>0)===true){result+=days+' day(s),';}if((hours>0)===true){result+=' '+hours+' hour(s), ';}if((minutes>0)===true){result+=' '+minutes+' minute(s),';}if((seconds>0)){result+=' '+seconds+' second(s),';}result=result.slice(0,-1);return result;}